package qat.parser.qashparser;// JDK importsimport java.io.File;// qat importsimport qat.parser.TestFinderInterface;import qat.parser.ParserInterface;public class QASHTestFinder extends Object implements TestFinderInterface {    /**     * This is the file extension this TestFinder uses to determine whether a file will be      * treated as a test or not.     */    private static final String TEST_FILE_SUFFIX            = ".qash";	    private String rootDirectory;	    public QASHTestFinder() {    }	    public void setProjectRoot(String rootDirectory) {	this.rootDirectory = rootDirectory;    }    /**     * Return true if this file is a QASH file.     */    public boolean isTestFile(File file) {	return ((file.getName().toLowerCase().endsWith(TEST_FILE_SUFFIX))&&		(!isExcludePath(file)));    }	    private boolean isExcludePath(File file) {	String path = file.getPath();	return ((path.indexOf("SCCS")>=0)|		(path.indexOf("deleted_files")>=0));    }	    /**     * Return a parser for the specified file type, or null     * if it's not one we recognise.     */    public ParserInterface getParser(File file) {	if (isTestFile(file))	    return new qat.parser.qashparser.QASHParser();	return null;    }}